import { useState } from 'react';
import ButtonGroup from 'react-bootstrap/ButtonGroup';
import ToggleButton from 'react-bootstrap/ToggleButton';
import { useDispatch } from 'react-redux';
import { actions as modalAction } from '../slices/Modals.js';

const ButtonsLink = () => {
  const [radioValue, setRadioValue] = useState('1');
  const dispatch = useDispatch();

  const trailer = () => {
    dispatch(modalAction.openModal({ type: 'rename' }));
  };
  const lore = (id) => {
    dispatch(modalAction.openModal({ type: 'remove', id }));
  };
  const gallery = () => {
    dispatch(modalAction.openModal({ type: 'gallery' }));
  };

  const radios = [
    { name: 'Главная', value: '1', page: '' },
    { name: 'История мира', value: '2', page: '' },
    { name: 'Галерея', value: '3', page: gallery() },
    { name: 'Скачать билд', value: '4', page: '' },
    { name: 'Помощь проекту', value: '5', page: '' },
  ];

  return (
    <ButtonGroup>
      {radios.map((radio, idx) => (
        <ToggleButton
          key={`${radio.value}`}
          id={`radio-${idx}`}
          type="radio"
          name="radio"
          variant="transparent"
          value={radio.value}
          checked={radioValue === radio.value}
          onChange={(e) => setRadioValue(e.currentTarget.value)}
          className={radioValue === radio.value ? 'text-danger' : 'text-white'}
          style={{ opacity: '1' }}
        >
          {radio.name}
        </ToggleButton>
      ))}
    </ButtonGroup>
  );
};

export default ButtonsLink;
