import React, { useState } from 'react';
import CarouselItem from '../useless/screenshots/screenShots.jsx';

const Gallery = () => {
  const [index, setIndex] = useState(0);
  const handleSelect = (selectedIndex) => {
    setIndex(selectedIndex);
  };

  return (
    <div className="container-fluid card shadow-sm bg-transparent bg-gradient">
      <div className="container-fluid row d-flex">
        <div className="container-fluid col-12 col-md-25 p-0 d-flex align-items-center justify-content-center">
          <CarouselItem index={index} handleSelect={handleSelect} />
        </div>
      </div>
    </div>
  );
};

export default Gallery;
