import { useState, useEffect } from 'react';
import ButtonGroup from 'react-bootstrap/ButtonGroup';
import ToggleButton from 'react-bootstrap/ToggleButton';
import { useDispatch } from 'react-redux';
import { actions as pagesAction } from '../slices/pagesSlice.js';

const ButtonsLink = () => {
  const dispatch = useDispatch();
  const [radioValue, setRadioValue] = useState('1');

  const radios = [
    { name: 'Главная', value: '1' },
    { name: 'История мира', value: '2' },
    { name: 'Галерея', value: '3' },
    { name: 'Скачать билд', value: '4' },
    { name: 'Помощь проекту', value: '5' },
  ];

  useEffect(() => {
    dispatch(pagesAction.openPage({ type: radioValue }));
  }, [dispatch, radioValue]);

  return (
    <ButtonGroup>
      {radios.map((radio, idx) => (
        <ToggleButton
          key={`${radio.value}`}
          id={`radio-${idx}`}
          type="radio"
          name="radio"
          variant="transparent"
          value={radio.value}
          checked={radioValue === radio.value}
          onChange={(e) => setRadioValue(e.currentTarget.value)}
          className={radioValue === radio.value ? 'text-danger' : 'text-white'}
          style={{ opacity: '1' }}
        >
          {radio.name}
        </ToggleButton>
      ))}
    </ButtonGroup>
  );
};

export default ButtonsLink;
