import React, { useState } from 'react';
import Carousel from 'react-bootstrap/Carousel';
import photos from '../useless/screenshots/screenShots.js';

const Gallery = ({ handleClose }) => {
  const [index, setIndex] = useState(0);
  const handleSelect = (selectedIndex) => {
    setIndex(selectedIndex);
  };
  const images = photos;

  const CarouselItem = ({ item }) => (
    <Carousel.Item><img src={item} alt={item} /></Carousel.Item>
  );

  return (
    <>
      <div className="fade modal-backdrop show bg-secondary bg-opacity-50" />
      <div role="dialog" aria-modal="true" style={{ display: 'block', margin: '69px 0px 0px 0px' }} className="fade modal show bg-secondary bg-opacity-50" tabIndex="-1">
        <div className="modal-header bg-secondary bg-opacity-50" style={{ height: '10px' }}>
          <button onClick={handleClose} type="button" aria-label="Close" data-bs-dismiss="modal" className="btn btn-close" />
        </div>
        <div className="modal-body">
          <Carousel activeIndex={index} onSelect={handleSelect}>
            {images.map((el) => (
              <CarouselItem
                item={el}
              />
            ))}
          </Carousel>
        </div>
      </div>
    </>
  );
};

export default Gallery;
